import React, { useState } from 'react';
import ProductBtn from './ProductBtn';
import * as enumerations from '../constants/enumerations';

export default function ProductStandouts() {
  const [numSelected, setNumSelected] = useState(0);
  const [disableBtns, setDisableBtns] = useState(false);

  useEffect(() => {
    if (numSelected === 5) {
      setDisableBtns(true);
    } else {
      setDisableBtns(false);
    }
  });

  return (
    <React.Fragment>
      <h3>Product Standouts</h3>
      <h4>Choose up to 5 that best apply (optional)</h4>
      <div className="square-standouts-container">
        <ProductBtn
          value={enumerations.productStandouts.accurateTimekeeping}
          setValue={setNumSelected}
        />
        <ProductBtn value={enumerations.productStandouts.highQuality} />
        <ProductBtn value={enumerations.productStandouts.durable} />
        <ProductBtn value={enumerations.productStandouts.elegant} />
        <ProductBtn value={enumerations.productStandouts.goodWeight} />
        <ProductBtn value={enumerations.productStandouts.versatile} />
        <ProductBtn value={enumerations.productStandouts.looksExpensive} />
        <ProductBtn value={enumerations.productStandouts.attractsCompliments} />
        <ProductBtn value={enumerations.productStandouts.unique} />
        <ProductBtn value={enumerations.productStandouts.greatGift} />
        <ProductBtn value={enumerations.productStandouts.greatValue} />
      </div>
    </React.Fragment>
  );
}
