import React, { useState } from 'react';
import NumberButton from './NumberButton';

export default function RatingBar(props) {
  const [rating, setRating] = useState(0);

  function handleRating(rating) {
    setRating(rating);
    console.log(rating);
  }

  return (
    <React.Fragment>
      <p className="rating-text">{props.name}</p>
      <div className="rating-bar-container">
        <NumberButton value={1} handleRating={handleRating} />
        <div className="connecting-bar"></div>
        <NumberButton value={2} handleRating={handleRating} />
        <div className="connecting-bar"></div>
        <NumberButton value={3} handleRating={handleRating} />
        <div className="connecting-bar"></div>
        <NumberButton value={4} handleRating={handleRating} />
        <div className="connecting-bar"></div>
        <NumberButton value={5} handleRating={handleRating} />
      </div>
    </React.Fragment>
  );
}
